import React, { useState } from "react";
import "./App.css";

const Coach = ({ seats, onSeatClick }) => {
  return (
    <div className="coach">
      {seats.map((seat) => (
        <Seat
          key={seat.number}
          seat={seat}
          onSeatClick={onSeatClick}
          isAvailable={seat.isAvailable}
        />
      ))}
    </div>
  );
};

const Seat = ({ seat, onSeatClick, isAvailable }) => {
  const handleClick = () => {
    if (isAvailable) {
      onSeatClick(seat.number);
    }
  };

  return (
    <div
      className={`seat ${isAvailable ? "available" : "unavailable"}`}
      onClick={handleClick}
    >
      {seat.number}
    </div>
  );
};

const App = () => {
  const totalSeats = 80;
  const seatsPerRow = 7;
  const lastRowSeats = 3;

  const [seats, setSeats] = useState(
    Array.from({ length: totalSeats }, (_, index) => ({
      number: index + 1,
      isAvailable: true
    }))
  );

  const handleSeatClick = (seatNumber) => {
    // Handle seat reservation logic here
    // Update the state to mark the seat as reserved
    const updatedSeats = seats.map((seat) =>
      seat.number === seatNumber ? { ...seat, isAvailable: false } : seat
    );
    setSeats(updatedSeats);
  };

  return (
    <div className="App">
      <h1 className="header">Train Seat Reservation</h1>
      <Coach seats={seats} onSeatClick={handleSeatClick} />
    </div>
  );
};

export default App;
