import React, { useState } from "react";
import "./App.css";

const Seat = ({ seat, onSeatClick }) => {
  const handleClick = () => {
    if (seat.isAvailable) {
      onSeatClick(seat.number);
    }
  };

  return (
    <div
      className={`seat ${seat.isAvailable ? 'available' : 'unavailable'}`}
      onClick={handleClick}
    >
      {seat.number}
    </div>
  );
};

const Coach = ({ seats, onSeatClick }) => {
  return (
    <div className="coach">
      {seats.map((row, index) => (
        <div className="row" key={index}>
          {row.map((seat) => (
            <Seat
              key={seat.number}
              seat={seat}
              onSeatClick={onSeatClick}
            />
          ))}
        </div>
      ))}
    </div>
  );
};

const App = () => {
  const totalSeats = 80;
  const seatsPerRow = 7;
  const lastRowSeats = 3;

  const generateSeats = () => {
    const seats = [];
    let seatNumber = 1;

    while (seatNumber <= totalSeats) {
      const row = [];

      for (let i = 0; i < seatsPerRow; i++) {
        if (seatNumber <= totalSeats) {
          row.push({ number: seatNumber, isAvailable: true });
          seatNumber++;
        }
      }

      seats.push(row);
    }

    return seats;
  };

  const [seats, setSeats] = useState(generateSeats());

  const handleSeatClick = (seatNumber) => {
    const updatedSeats = seats.map((row) =>
      row.map((seat) =>
        seat.number === seatNumber ? { ...seat, isAvailable: false } : seat
      )
    );
    setSeats(updatedSeats);
  };

  return (
    <div className="App">
      <h1 className="header">Train Seat Reservation</h1>
      <Coach seats={seats} onSeatClick={handleSeatClick} />
    </div>
  );
};

export default App;
